mapper.select:
SELECT 

FROM assist a

WHERE a.id = #{id}

SELECT 

FROM assist a




		AND a.id = #{id}


		AND a.applyDate = #{applyDate}


		AND a.applyName LIKE 
			'%'||#{applyName}||'%'
			'%'+#{applyName}+'%'
			concat('%',#{applyName},'%')


		AND a.applyReason LIKE 
			'%'||#{applyReason}||'%'
			'%'+#{applyReason}+'%'
			concat('%',#{applyReason},'%')


		AND a.assistState = #{assistState}


		AND a.applyDate BETWEEN #{beginApplydate} AND #{endApplydate}




		ORDER BY ${page.orderBy}

SELECT 

FROM assist a






		ORDER BY ${page.orderBy}
