SELECT 

FROM userinfo a




		AND a.idCard = #{idCard}


		AND a.community = #{community}


		AND a.communityKey = #{communityKey}


		AND a.degreeOfEducation = #{degreeOfEducation}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}

